package com.abc.apptracnghiem.Question;public class Question {    private int id;    private String question;    private String ans_a;    private String ans_b;    private String ans_c;    private String ans_d;    private String result;    private String explaintion;    private int type_question;    private String img;    private String answer;    public int choiceId=-1;    public Question(int id, String question, String ans_a, String ans_b, String ans_c, String ans_d, String result, String explaintion, int type_question, String img,String answer) {        this.id = id;        this.question = question;        this.ans_a = ans_a;        this.ans_b = ans_b;        this.ans_c = ans_c;        this.ans_d = ans_d;        this.result = result;        this.explaintion = explaintion;        this.type_question = type_question;        this.img = img;        this.answer=answer;    }    public Question() {    }    public String getAnswer() {        return answer;    }    public void setAnswer(String answer) {        this.answer = answer;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getQuestion() {        return question;    }    public void setQuestion(String question) {        this.question = question;    }    public String getAns_a() {        return ans_a;    }    public void setAns_a(String ans_a) {        this.ans_a = ans_a;    }    public String getAns_b() {        return ans_b;    }    public void setAns_b(String ans_b) {        this.ans_b = ans_b;    }    public String getAns_c() {        return ans_c;    }    public void setAns_c(String ans_c) {        this.ans_c = ans_c;    }    public String getAns_d() {        return ans_d;    }    public void setAns_d(String ans_d) {        this.ans_d = ans_d;    }    public String getResult() {        return result;    }    public void setResult(String result) {        this.result = result;    }    public String getExplaintion() {        return explaintion;    }    public void setExplaintion(String explaintion) {        this.explaintion = explaintion;    }    public int getType_question() {        return type_question;    }    public void setType_question(int type_question) {        this.type_question = type_question;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }}