package com.abc.apptracnghiem.Question;import android.content.Context;import android.database.Cursor;import android.database.sqlite.SQLiteDatabase;import java.util.ArrayList;public class TypeQuestionController {    private DBHelper dbHelper;    public TypeQuestionController(Context context) {        dbHelper = new DBHelper(context);    }    public ArrayList<TypeQuestion> getTypeQuestions(String topic) {        ArrayList<TypeQuestion> typeQuestions = new ArrayList<>();        SQLiteDatabase database = dbHelper.getReadableDatabase();        Cursor cursor = database.rawQuery("SELECT * FROM type_question WHERE topic='"+topic+"'", null);        cursor.moveToFirst();        do {            TypeQuestion item = new TypeQuestion(cursor.getInt(0), cursor.getString(1), cursor.getString(2));            typeQuestions.add(item);        } while (cursor.moveToNext());        return typeQuestions;    }}