package com.abc.apptracnghiem.Topic;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.ImageView;import android.widget.TextView;import com.abc.apptracnghiem.R;import java.util.ArrayList;public class ExamAdapter extends ArrayAdapter<Exam> {    public ExamAdapter(Context context, ArrayList<Exam> exams) {        super(context, 0, exams);    }    public View getView(int position, View convertView, ViewGroup parent) {        if (convertView == null) {            LayoutInflater inflater = LayoutInflater.from(getContext());            convertView = inflater.inflate(R.layout.item_gridview, parent, false);        }        TextView tvName = convertView.findViewById(R.id.tvNumExam);        ImageView imageView = convertView.findViewById(R.id.imgIcon);        Exam exam = getItem(position);        if (exam != null) {            tvName.setText(""+exam.getName());            imageView.setImageResource(R.drawable.checklist_thumbnail);        }        return convertView;    }}