package com.abc.apptracnghiem.Topic;import android.content.Context;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.abc.apptracnghiem.Question.Question;import com.abc.apptracnghiem.R;import java.util.ArrayList;import java.util.List;public class TopicAdapter extends BaseAdapter {    private ArrayList<Topic> listData;    private LayoutInflater layoutInflater;    private Context context;    public TopicAdapter(Context aContext,  ArrayList<Topic> listData) {        this.context = aContext;        this.listData = listData;        layoutInflater = LayoutInflater.from(aContext);    }    @Override    public int getCount() {        return listData.size();    }    @Override    public Object getItem(int position) {        return listData.get(position);    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        ViewHolder holder;        if (convertView == null) {            convertView = layoutInflater.inflate(R.layout.topic_gridview, null);            holder = new ViewHolder();            holder.imgTopic =  convertView.findViewById(R.id.imvTopic);            holder.tvTopic = convertView.findViewById(R.id.tvTopic);            convertView.setTag(holder);        } else {            holder = (ViewHolder) convertView.getTag();        }        Topic topic = this.listData.get(position);        holder.tvTopic.setText(topic.getName());        holder.imgTopic.setImageResource(topic.getImg());        return convertView;    }    static class ViewHolder {        ImageView imgTopic;        TextView tvTopic;    }}